///<reference path="../../helpers/AdapterBase.ts" />
///<reference path="../../views/controls/Label.ts" />

namespace AFC.Globals {
    export interface ILabelProvider extends Tools.INotifier {
        readonly label: string;
    }
    
    export class LabelAdapter extends Tools.AdapterBase<ILabelProvider> {
        public readonly control: Controls.Label;
        
        constructor(label: Controls.Label) {
            super();
            this.control = label;
        }
        
        protected _update(model: ILabelProvider):void {
            this.control.setHTML(model.label);
        }
    }
}