///<reference path="../../helpers/AdapterBase.ts" />
///<reference path="../../views/controls/Label.ts" />

namespace AFC.Globals {
    export interface IElemProvider extends Tools.INotifier {
        readonly [key: string]: any;
    }

    export class TemplateElemAdapter extends Tools.AdapterBase<IElemProvider> {
        public readonly control: Controls.TemplateElem;
        public onClear: () => any;
        public onGenerate: () => any;

        constructor(elem: Controls.TemplateElem) {
            super();
            this.control = elem;
        }

        protected _update(model: IElemProvider): void {
            this.control.empty();
            this.control.generateContent(model);
            this.onGenerate && this.onGenerate();
        }
    }
}