namespace AFC.Application {
    export class Inflater {
        public inflate(resource: string, root: gui.IView): gui.IView {
            let div = new gui.AfcGuiDiv();
            div.innerHTML = resource;
            div.className = 'InflatedFragment';
            return div;
        }
    }
}