namespace AFC {
    export namespace BulkMail {

        export class BMCampaignFactory {
            public static createCampaign(item?: ICampaignRestData | ICampaignSMSRestData, isNew?: boolean) {
                if (item && item.channel === BMCampaignChannel.sms) {
                    return new BMCampaignSMS(item as ICampaignSMSRestData, isNew);
                }
                if (item && item.channel === BMCampaignChannel.social) {
                    return new BMCampaignSocial(item as ICampaignSocialRestData, isNew);
                }

                return new BMCampaignEDDM(item, isNew);
            }
        }

    }
};