function getUSStringForDate(date)
{
    var res = null;

    if (date)
    {
        var day = date.getDate()<10?'0'+date.getDate().toString():date.getDate().toString();
        var month = date.getMonth()+1;
        month = month<10?'0'+month.toString():month.toString();
        var year = date.getFullYear().toString();
        res = month +'/'+ day +'/'+ year;
    }
    return res;
};

function getStringForDate(date)
{
    var res = null;

    if (date)
    {
        var day = date.getDate()<10?'0'+date.getDate().toString():date.getDate().toString();
        var month = date.getMonth();
        var year = date.getFullYear().toString();

        res = day + ' ';
        switch(month)
        {
            case 0:
                res += 'janvier';
                break;
            case 1:
                res += 'février';
                break;
            case 2:
                res += 'mars';
                break;
            case 3:
                res += 'avril';
                break;
            case 4:
                res += 'mai';
                break;
            case 5:
                res += 'juin';
                break;
            case 6:
                res += 'juillet';
                break;
            case 7:
                res += 'août';
                break;
            case 8:
                res += 'septembre';
                break;
            case 9:
                res += 'octobre';
                break;
            case 10:
                res += 'novembre';
                break;
            case 11:
                res += 'décembre';
                break;
        }
        res += ' ' + year;
    }
    return res;
};